import { useState, useEffect } from "react"
import { SanityClient } from "../client"

export default function Post() {

        const [postData, setPost] = useState(null);

        useEffect(() => {
            SanityClient.fetch(`*[_type == "post"]{
                title, 
                slug, 
                mainImage{
                    asset->{
                        _id,
                        url
                    },
                    alt
                }
            }`)
            .then((data) => setPost(data))
            .catch(console.error)
        }, [])

        return (
            <main>
                <section>
                    <h1></h1>
                    <h2></h2>
                    <div></div>
                </section>

            </main>
        )
}
